import React from "react";
import styled from 'styled-components';
import axios from 'axios';
import { Link, useNavigate } from "react-router-dom";
import { useEffect, useContext, useState } from 'react';

export default function LoginScreen(){

    const [email, setEmail]=useState("");
    const [password,setPassword]=useState("");

    function Login(event){

        event.preventDefault();
        const info={
            email:email,
            password:password
        }
    }

    return(

        <Container>
            <img src="trackitlogo.png" alt="logo"/>
            <Form onSubmit={Login}>
                <input type="email" id="email" value={email} placeholder="email" required onChange={(e)=>setEmail(e.target.value)} />
                <input type="password" id="password" value={password} placeholder="senha" required onChange={(e)=>setPassword(e.target.value)} />
                <button type="submit" >Entrar</button>
            </Form>
            <Link to='/cadastro'>NÃ£o tem uma conta? Cadastre-se</Link>
        </Container>

    )
}

const Container=styled.div`
    display: flex;
    align-items: center;
    flex-direction: column;
    width: 100%;
    margin-top: 68px;

    font-family: 'Lexend Deca', sans-serif;
    img{
        width: 180px;
        height: 178px;
        margin-bottom: 35px;
    }
    a{
        margin-top: 25px;
        font-family: 'Lexend Deca';
        font-style: normal;
        font-weight: 400;
        font-size: 13.976px;
        line-height: 17px;
        text-align: center;
        text-decoration-line: underline;

        color: #52B6FF;
    }

`
const Form = styled.form`
    display: flex;
    flex-direction: column;
    width: 100%;
    margin-right: 36px;
    margin-left: 36px;

    input {
        height: 45px;
        margin-right: 36px;
        margin-left: 36px;
        min-width: 303px;
        margin-bottom: 6px;
        border-radius: 5px;
        border: 1px solid #D4D4D4; 
        padding-left:11px ;
    }
    input:focus {
        border: none
    }
    input::placeholder {
        color: #DBDBDB;
        font-size: 20px;
        text-indent: 10px;
    }
    div {
        display: flex;
        justify-content: center;
    }
    button {
        min-width: 303px;
        height: 45px;
        margin-right: 36px;
        margin-left: 36px;
        text-align: center;
        background-color: #52B6FF;
        color: #FFFFFF;
        font-size: 21px;
        border: none;
        border-radius: 5px;
        display: flex;
        justify-content: center;
        align-items: center;
        a{
            text-decoration: none;
        }
    }
`
