import React from "react";
import styled from 'styled-components';
import axios from 'axios';
//import { Link, useNavigate } from "react-router-dom";
import { useEffect,useContext , useState } from 'react';
import UserContext from "../context/UserContext";
import Header from "./Header";
import Habit from "./Habit";
import Footer from "./Footer";
import NewHabit from "./Newhabit";
import { ThreeDots } from "react-loader-spinner";


export default function HabitsPage(){
    const { user } = useContext(UserContext);

    const {image,token} = user;
    const [habits,setHabits]=useState([]);
    //const navigate = useNavigate();
    // navigate("/");
    const [hasHabit, sethasHabit] = useState(true);

    useEffect(() => {
        const config = {
            headers: {
              Authorization: `Bearer ${token}`
            }
        };

        const promise = axios.get(`https://mock-api.bootcamp.respondeai.com.br/api/v2/trackit/habits`,config);

        promise.then(resposta => {
            console.log(resposta.data);
            setHabits(...habits,resposta.data);
            sethasHabit(false);
        });

    }, []);

    return(
        <>
        <Header/>
        <Page>
        <Container> <h1>Meus hábitos</h1> <Add>+</Add> </Container>
        <NewHabit/>
        {hasHabit ? 
        (
            <DummyText> Você não tem nenhum hábito cadastrado ainda. Adicione um hábito para começar a trackear! </DummyText>
        ):(
            <Column>
                {habits.map((habit) => (
                    <Habit habit={habit} token={token} key={habit.id}/>
                ))}
            </Column>
        )}
        </Page>
        <Footer/>
        </>
    )
}

const Column=styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;
    width: 100%;
    height: calc(100vh - 140px);
    background: #E5E5E5;
    margin: 20px;
`

const Page=styled.div`
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;
    width: 100%;
    height: calc(100vh - 140px);
    background: #E5E5E5;
    //margin-top: 70px;
`
const Container=styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 95%;
    margin-top: 20px;
    font-family: 'Lexend Deca', sans-serif;
    h1{
        font-family: 'Lexend Deca';
        font-style: normal;
        font-weight: 400;
        font-size: 22.976px;
        line-height: 29px;
        color: #126BA5;
    }
`
const Add=styled.div`
    width: 40px;
    height: 35px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: #52B6FF;
    border-radius: 4.63636px;
    font-size: 25px;
    color: #ffffff;
    padding-bottom: 4px;
`
