import React from "react";
import styled from 'styled-components';
import axios from 'axios';
import { useEffect,useContext , useState } from 'react';
import UserContext from "../context/UserContext";
import Header from "./Header";
import Habit from "./Habit";
import Footer from "./Footer";
import Newhabit from "./Newhabit";

export default function HabitsPage(){
    const { user } = useContext(UserContext);

    const {image,token} = user;
    const [habits,setHabits]=useState([]);


    const [hasHabit, sethasHabit] = useState(true);
    const [add, setAdd] = useState(false);
    useEffect(() => {
        const config = {
            headers: {
              Authorization: `Bearer ${token}`
            }
        };

        const promise = axios.get(`https://mock-api.bootcamp.respondeai.com.br/api/v2/trackit/habits`,config);

        promise.then(resposta => {
            console.log(resposta.data);
            setHabits(...habits,resposta.data);
            sethasHabit(false);
        });

    }, []);

    return(
        <>
        <Header/>
        <Page>
        <Container> <h1>Meus hábitos</h1> <Add onClick={() => setAdd(!add)}>+</Add> </Container>
        {add ? (<NewHabit setAdd={setAdd}/>):(<></>)}
        {hasHabit ? 
        (
            <EmptyText> Você não tem nenhum hábito cadastrado ainda. Adicione um hábito para começar a trackear! </EmptyText>
        ):(
            <Column>
                {habits.map((habit) => (
                    <Habit habit={habit} token={token} key={habit.id}/>
                ))}
            </Column>
        )}
        </Page>
        <Footer/>
        </>
    )
}

const Column=styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;
    height: calc(100vh - 140px);
    background: #E5E5E5;
    margin: 20px;
    margin-bottom: 35px;
`
const EmptyText=styled.div`
    width: 88%;
    font-family: 'Lexend Deca';
    font-style: normal;
    font-weight: 400;
    font-size: 17.976px;
    line-height: 22px;
    color: #666666;
`
const Page=styled.div`
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-around;
    width: 100%;
    height: calc(100vh - 140px);
    background: #E5E5E5;
`
const Container=styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 95%;
    margin-top: 20px;
    font-family: 'Lexend Deca', sans-serif;
    h1{
        font-family: 'Lexend Deca';
        font-style: normal;
        font-weight: 400;
        font-size: 22.976px;
        line-height: 29px;
        color: #126BA5;
    }
`
const Add=styled.div`
    width: 40px;
    height: 35px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: #52B6FF;
    border-radius: 4.63636px;
    font-size: 25px;
    color: #ffffff;
    padding-bottom: 4px;
`
